import Link from "next/link";
import ReactMarkdown from "react-markdown";

const BlogList = (props) => {

  function truncateSummary(content) {
    return content.slice(0, 200).trimEnd();
  }

  function reformatDate(fullDate) {
    const date = new Date(fullDate)
    return date.toDateString().slice(4);
  }

  return (
    <>
      <div className="container" style={{ display: 'block' }}>
        {props.allBlogs.length > 1 && props.allBlogs.map(post => (
          <div className="block">
            <Link
              key={post.slug}
              href={{ pathname: `/blog/${post.slug}` }}
            >
              <div>
                <a><h2 className="red-dark">{post.document.data.title}</h2></a>
                <h3 className="red"> {post.document.data.author} | {reformatDate(post.document.data.date)}</h3>
                <p className="gray-dark">
                  {post.document.data.summary}
                </p>
              </div>
            </Link>
          </div>
        ))}
      </div>

    </>
  );
};

export default BlogList;
